import React from "react";


export default class AllListItems extends React.Component{
    constructor(props){
        super(props);

        this.deleteItem= this.deleteItem.bind(this);

    
      };
        deleteItem(){
            console.log("DELETE")
        }
render()
{ 

    return (
        <div>              
        {this.props.tasks.map(function(task, index){
            return(   
                <div>        
            <div key={index} className='task-wrapper flex-wrapper'>{index+1} - {task.title}  <button onClick={this.deleteItem} className="close">X</button></div>
          
            </div> 
            )
          })}
        </div>  
     )

}
}
