import logo from './logo.svg';
import './App.css';

import React, { Component } from 'react'

class App extends Component {
  constructor(props){
    super(props);
    this.state = {
      todoList:[],
      activeItem:{
        id:null,
        title:"",
        completed:false,
      },
      editing:false,
    };
    this.fetchTasks= this.fetchTasks.bind(this);
    this.handleChange= this.handleChange.bind(this);
    this.formSubmit= this.formSubmit.bind(this);

  };

  getCookie(name) {
    var cookieValue = null;
    if (document.cookie && document.cookie !== '') {
        var cookies = document.cookie.split(';');
        for (var i = 0; i < cookies.length; i++) {
            var cookie = cookies[i].trim();
            // Does this cookie string begin with the name we want?
            if (cookie.substring(0, name.length + 1) === (name + '=')) {
                cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
                break;
            }
        }
    }
    return cookieValue;
}

  componentWillMount(){
    this.fetchTasks();
  }

  fetchTasks(){
    console.log("Fetching...")
    fetch('http://127.0.0.1:8000/todolist')
    .then(response => response.json())
    .then(data => 
      console.log("DATA:" ,data)

      )

  }

  formSubmit(e){
    e.preventDefault()
    if(this.state.activeItem.title === ''){
      console.log("Bu alan boş bırakılamaz")
    }
    else{

      console.log("Submit...")
      console.log(this.state.activeItem.title)
      var csrftoken = this.getCookie('csrftoken')

      var url = 'http://127.0.0.1:8000/todolist/'

      // if(this.state.editing === true){
      //   url = `http://127.0.0.1:8000/api/task-update/${ this.state.activeItem.id}/`
      //   this.setState({
      //     editing:false
      //   })
      // }



      fetch(url, {
        method:'POST',
        headers:{
          'Content-type':'application/json',
          'X-CSRFToken':csrftoken,
        },
        body:JSON.stringify(this.state.activeItem)
      }).then((response)  => {
          this.fetchTasks()
          this.setState({
             activeItem:{
            id:null, 
            title:'',
            completed:false,
          }
          })
      }).catch(function(error){
        console.log('ERROR:', error)
      })



    }

  }


  handleChange(e){

    var value = e.target.value
    if(value === ''){
      this.setState(
        {
          activeItem:{
          title:value
        }
        }
      )
      console.log("Bu alan boş bırakılamaz")
    }
    else{
      console.log('Value:', value)
      this.setState(
        {
          activeItem:{
          title:value
        }
        }
      )
    }


  }

  render() {
    return (
      <>
        <nav className='navbar bg-dark text-white'>
        <h4><img src={logo} alt="react-logo" className='r-logo' />REACT</h4>
        </nav>
      <div className='container'>
        <div className="row">
            <div className="col-md-3">
              </div>


            <div className="col-md-6">
                  <div className="task-container">

                        <div className="form-wrapper">
                        <h5>Todo App </h5>
                                <form onSubmit={this.formSubmit} action="" className='mt-5'>
                                <div className="flex-wrapper">

                                  <input onChange={this.handleChange}  className='form-control'  type="text" />

                                  <button className='btn btn-dark'>Submit</button>

                                  </div>
                                </form>
                              <div>
                                <ul>
                                  <li>
                                    deneme
                                  </li>
                                </ul>
                              </div>
                        </div>
                  </div>
              </div>

          </div>
      </div>


      </>
    )
  }
}



export default App;
