import React, { Component } from 'react'

export class Register extends Component {
    constructor(props){
        super(props);
          this.state = {
            register:{
              username:'',
              email:'',
              password:'',
            },
    
          }
    
    
          this.handlePassword = this.handlePassword.bind(this)
          this.ge = this.checkUsername.bind(this)
          this.checkUsername = this.checkUsername.bind(this)
          this.handleEmail = this.handleEmail.bind(this)
          this.handleUsername = this.handleUsername.bind(this)
          this.handleSubmit = this.handleSubmit.bind(this)
          this.getCookie = this.getCookie.bind(this)
    
    
      };
      
  getCookie(name) {
    var cookieValue = null;
    if (document.cookie && document.cookie !== '') {
        var cookies = document.cookie.split(';');
        for (var i = 0; i < cookies.length; i++) {
            var cookie = cookies[i].trim();
            // Does this cookie string begin with the name we want?
            if (cookie.substring(0, name.length + 1) === (name + '=')) {
                cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
                break;
            }
        }
    }
    return cookieValue;
}


  getUsername(username){
       

    
        fetch('http://127.0.0.1:8000/account/username_check')
        .then(response => response.json())
        .then(data => 
          //console.log(data)
          {data.map(function(user, index){
            return(
             
             console.log(user.username) 
              
              )})}
        )
        console.log(this.state.register)
    }

  handleUsername(e){

  
    var username = e.target.value

    console.log('Username:', username)


    this.setState({
        register:{
        ...this.state.register,
        username:username,

      }
    })
 
    this.getUsername(username)
  }
  handleEmail(e){


    var email = e.target.value

    console.log('Email:', email)


    this.setState({
        register:{
        ...this.state.register,
        email:email,

      }
    })
  }

  handlePassword(e){

    var password = e.target.value

    console.log('Password:', password)

    this.setState({
        register:{
        ...this.state.register,

        password:password
      }
    })
  
  }


  handleSubmit(e){
    e.preventDefault()
    console.log(this.state.register)
    console.log('ITEM:', this.state.register)

    var csrftoken = this.getCookie('csrftoken')

    var url = 'http://127.0.0.1:8000/account/register'

    fetch(url, {
      method:'POST',
      headers:{
        'Content-type':'application/json',
        'X-CSRFToken':csrftoken,
      },
      body:JSON.stringify(this.state.login)
    }).then((response)  => {
        console.log(response)
        this.setState({
           login:{
          username:'',
          email:'', 
          password:'',

        }
        })
    }).catch(function(error){
      console.log('ERROR:', error)
    })

  }
  render() {
    return (
        <>
        <div className="container">
          <div className="row">
            <div className="col-md-3"></div>
            <div className="col-md-6">
            <h3>Register</h3>
              <form action="" onSubmit={this.handleSubmit}>
                <div className="form-group">
                <label htmlFor="id_username">Username</label>
                <input className="form-control "  onChange={this.handleUsername} type="text" name="username" maxLength="150" autoFocus=""  id="id_username" /> 

                </div>

                <div className="form-group">
                <label htmlFor="id_email">Email</label>
                <input className="form-control"  onChange={this.handleEmail} type="email" name="email" required="" id="id_email" />

                </div>

                <div className="form-group">
                <label htmlFor="id_password">Password</label>

                <input className="form-control"  onChange={this.handlePassword} type="password" name="password" required="" id="id_password" />
                </div>


                <div className="form-group">
                <button className='btn btn-dark w-100'>Register</button>
                </div>
              </form>
            </div>
          </div>
        </div>
          
  </>
    )
  }
}

export default Register
