export class Layout extends Component {
    constructor(props){
        super(props);
        this.state = {
        current_user:null
        }
        this.fetchTasks = this.fetchTasks.bind(this)
    
      };
      componentWillMount(){
        this.currentUser()
      }
      currentUser(){
        console.log('Fetching...')
        fetch('http://127.0.0.1:8000/current_user')
        .then(response => response.json())
        .then(data => 
        this.setState({ current_user: data })
          //  console.log(data)
         
          )
      }
    
render() {
  return (
    <>
      <Navbar  currentUser= {this.currentUser}/>
      <Outlet />
    </>
  );
};
}

export default Layout;
