import React, { Component } from 'react'

export class Login extends Component {
  constructor(props){
    super(props);
      this.state = {
        login:{
          username:'',
          password:'',
        },

      }


      this.handlePassword = this.handlePassword.bind(this)
      this.handleUsername = this.handleUsername.bind(this)
      this.handleSubmit = this.handleSubmit.bind(this)
      this.getCookie = this.getCookie.bind(this)


  };

  getCookie(name) {
    var cookieValue = null;
    if (document.cookie && document.cookie !== '') {
        var cookies = document.cookie.split(';');
        for (var i = 0; i < cookies.length; i++) {
            var cookie = cookies[i].trim();
            // Does this cookie string begin with the name we want?
            if (cookie.substring(0, name.length + 1) === (name + '=')) {
                cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
                break;
            }
        }
    }
    return cookieValue;
}

  handleUsername(e){

    console.log(e.target)
    var name = e.target.name
    var username = e.target.value
    console.log('Name:', name)
    console.log('Username:', username)


    // this.setState({
    //   login:{
    //     ...this.state.login,
    //     username:username,

    //   }
    // })
  }

  handlePassword(e){
    
    var password = e.target.value

    console.log('Password:', password)

    // this.setState({
    //   login:{
    //     ...this.state.login,

    //     password:password
    //   }
    // })
  }


  handleSubmit(e){
    e.preventDefault()
    console.log('ITEM:', this.state.login)

    var csrftoken = this.getCookie('csrftoken')

    var url = 'http://127.0.0.1:8000/createItem'

    if(this.state.editing == true){
      url = `http://127.0.0.1:8000/updateItem/${this.state.activeItem.id}`
      this.setState({
        editing:false
      })
    }



    fetch(url, {
      method:'POST',
      headers:{
        'Content-type':'application/json',
        'X-CSRFToken':csrftoken,
      },
      body:JSON.stringify(this.state.activeItem)
    }).then((response)  => {
        this.fetchTasks()
        this.setState({
           activeItem:{
          id:null, 
          title:'',
          completed:false,
        }
        })
    }).catch(function(error){
      console.log('ERROR:', error)
    })

  }
  render() {
    return (
      <>
      <div className="container">
        <div className="row">
          <div className="col-md-3"></div>
          <div className="col-md-6">
          <h3>Login</h3>
            <form action="" onSubmit={this.handleSubmit} >
              <div className="form-group">
              <label htmlFor="id_username">Username</label>
              <input  onChange={this.handleUsername} type="text"  className="form-control"  name="username"  value={this.state.login.username} autoFocus=""  required="" id="id_username"/>
              </div>
              <div className="form-group">
              <label htmlFor="id_password">Password</label>
              <input  onChange={this.handlePassword} className="form-control " type="password" name="password"  value={this.state.login.password} required="" id="id_password" />

              </div>
              <div className="form-group">
              <button className='btn btn-dark w-100' type="submit">Login</button>
              </div>
            </form>
          </div>
        </div>
      </div>
        
</>
    )
  }
}

export default Login
