import React, {useState, useEffect} from 'react'
import APIService from '../APIService';
// import {useCookies} from 'react-cookie';
// import {useHistory} from 'react-router-dom';
function Home() {
  const [title, setTitle] = useState('')

  const [todoList, setTodo] = useState([])
  const [editTodo, setEditTodo] = useState(null)
  const [editing, setEditing] = useState(false)
  //const [token, setToken, removeToken] = useCookies(['mytoken'])

  //let history = useHistory()

  useEffect(() => {
    fetch('http://127.0.0.1:8000/api/todo/', {
      'method':'GET',
      headers: {
        'Content-Type':'application/json',
       // 'Authorization':`Token ${token['mytoken']}` 
      }
    })
    .then(resp => resp.json())
    .then(resp => setTodo(resp))
    .catch(error => console.log(error))

  }, [])


  useEffect(() => {
    // if(!token['mytoken']) {
    //     //history.push('/')
    //     //window.location.href = '/'
       
    // }
}, [])



 const getCookie = (name)  => {
    var cookieValue = null;
    if (document.cookie && document.cookie !== '') {
        var cookies = document.cookie.split(';');
        for (var i = 0; i < cookies.length; i++) {
            var cookie = cookies[i].trim();
            // Does this cookie string begin with the name we want?
            if (cookie.substring(0, name.length + 1) === (name + '=')) {
                cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
                break;
            }
        }
    }
    return cookieValue;
}



//  const fetchTasks = () => {
//     console.log('Fetching...')


//     fetch('http://127.0.0.1:8000/api/todo/')
//     .then(response => response.json())
//     .then(data => 
//       this.setState({
//         todoList:data
//       })
//       )
//   }

//  const handleChange = (e) =>{
//     var name = e.target.name
//     var value = e.target.value
//     console.log('Name:', name)
//     console.log('Value:', value)

//     // this.setState({
//     //   activeItem:{
//     //     ...this.state.activeItem,
//     //     title:value
//     //   }
//     //})
//   }

 const handleSubmit = (e) => {
    e.preventDefault()
    console.log('ITEM:', this.state.activeItem)
    var csrftoken = this.getCookie('csrftoken')
    var url = 'http://127.0.0.1:8000/api/todo/'
    if(editing == true){
      console.log("EDİTİNG TRUE")
      url = `http://127.0.0.1:8000/updateItem/${this.state.activeItem.id}`
      editing(false)

    }



    fetch(url, {
      method:'POST',
      headers:{
        'Content-type':'application/json',
        'X-CSRFToken':csrftoken,
      },
      body:JSON.stringify(this.state.activeItem)
    }).then((response)  => {
        this.fetchTasks()
        this.setState({
           activeItem:{
          id:null, 
          title:'',
          completed:false,
        }
        })
    }).catch(function(error){
      console.log('ERROR:', error)
    })

  }

 const startEdit = (task) =>{
  console.log("EDIT")
    setTitle(task.title)
    setEditing(true)

  }


 const  deleteItem =  (task) =>{
    var csrftoken = this.getCookie('csrftoken')

    fetch(`http://127.0.0.1:8000/deleteItem/${task.id}`, {
      method:'DELETE',
      headers:{
        'Content-type':'application/json',
        'X-CSRFToken':csrftoken,
      },
    }).then((response) =>{

      this.fetchTasks()
    })
  }


  const strikeUnstrike = (task) => {

    task.completed = !task.completed
    var csrftoken = this.getCookie('csrftoken')
    var url = `http://127.0.0.1:8000/updateItem/${task.id}`

      fetch(url, {
        method:'POST',
        headers:{
          'Content-type':'application/json',
          'X-CSRFToken':csrftoken,
        },
        body:JSON.stringify({'completed': task.completed, 'title':task.title})
      }).then(() => {
        this.fetchTasks()
      })

    console.log('TASK:', task.completed)
  }

  // var tasks = this.state.todoList
  var self = this
  return (
    <>
    <div className="container mt-5">
    <div className="row">
      <div className="col-md-3"></div>
      <div className="col-md-6 table_todo">
      <div id="task-container  ">
         <h3 className='mt-3'>Todo App</h3>
          <div  id="form-wrapper" className='mt-3'>
             <form onSubmit={handleSubmit}  id="form">
                <div className="flex-wrapper">
                    <div style={{flex: 6}}>
                        <input onChange={e => setTitle(e.target.value)} className="form-control" id="title" value={title} type="text" name="title" placeholder="Add task.." />
                     </div>

                     <div style={{flex: 1}}>
                        <input id="submit" className="btn btn-dark" type="submit" name="Add" />
                      </div>
                  </div>
            </form>
         
          </div>

          <div  id="list-wrapper" className='mt-3'>  
                 
                {todoList.map(function(task, index){
                  return(

    
                      <div key={index} className="task-wrapper flex-wrapper">

                        <div onClick={() => self.strikeUnstrike(task)} style={{flex:7}}>

                            {task.completed == false ? (
                                <span>{task.title}</span>

                              ) : (

                                <strike>{task.title}</strike>
                              )}

                        </div>

                        <div style={{flex:1}}>
                            <button onClick={() => startEdit(task)} className="btn btn-sm btn-outline-info">Edit</button>
                        </div>

                        <div style={{flex:1}}>
                            <button onClick={() => deleteItem(task)} className="btn btn-sm btn-outline-dark delete">-</button>
                        </div>
                                 
                      </div>
                    )

                })}
                
          </div>
          </div>
      </div>
      </div>
    </div>
    </>
  )
}

export default Home








