import Alert from '../Alert';
import APIService from '../APIService';

import React, {useState, useEffect} from 'react'
import {useCookies} from 'react-cookie';

import {useNavigate} from 'react-router-dom';
function Register() {
  const [username, setUsername] = useState('')
  const [password, setPassword] = useState('')
  const [all_users, setAllUsers] = useState([])
  //const [email, setEmail] = useState('')
  const [token, setToken] = useCookies(['mytoken'])
  let navigate  = useNavigate()


  useEffect(() => {
    if(token['mytoken'] !== undefined) {
      navigate('/');
    }
}, [token])


  const getCookie =(name) => {
    var cookieValue = null;
    if (document.cookie && document.cookie !== '') {
        var cookies = document.cookie.split(';');
        for (var i = 0; i < cookies.length; i++) {
            var cookie = cookies[i].trim();
            // Does this cookie string begin with the name we want?
            if (cookie.substring(0, name.length + 1) === (name + '=')) {
                cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
                break;
            }
        }
    }
    return cookieValue;
}

const getUsername =(username) =>{
  
  fetch('http://127.0.0.1:8000/account/username_check')
  .then(response => response.json())
  .then(data => 

    setAllUsers(data),
    console.log(all_users)
  )
  checkUsername(username)
}

const checkUsername = (username) =>{
let user_name=username
let db_username=all_users
                  
{db_username.map(function(user, index){
  if(user.username == user_name){
    //console.log("burda")
    APIService.setAlert('This username is already taken.','danger');
  }

})}

}







 const handleSubmit = (e) =>{
    e.preventDefault()
    var csrftoken = getCookie('csrftoken')
    let body = {username,password}
    let body_login = {username,password}
    if (username == '' || password == ''){

    }
    else{

    APIService.RegisterUser(body,csrftoken)
    .then(resp => APIService.LoginUser(body_login))
    .then(resp => setToken('mytoken',resp.token))
    .catch(error => console.log(error))
    }

  }
  return (
    <>
    <div className="container">
      <div className="row">
        <div className="col-md-3"></div>
        <div className="col-md-6">
        <h3>Register</h3>
          <form action="" onSubmit={handleSubmit}>
            <div className="form-group">
            <label htmlFor="id_username">Username</label>
            <input className="form-control "  onChange={e => setUsername(e.target.value),getUsername()} type="text" name="username" maxLength="150" autoFocus=""  id="id_username" /> 
            < Alert alert= {alert}/>
            </div>
{/* 
            <div className="form-group">
            <label htmlFor="id_email">Email</label>
            <input className="form-control"  onChange={e => setEmail(e.target.value)} type="email" name="email" required="" id="id_email" />

            </div> */}

            <div className="form-group">
            <label htmlFor="id_password">Password</label>

            <input className="form-control"  onChange={e => setPassword(e.target.value)} type="password" name="password" required="" id="id_password" />
            </div>


            <div className="form-group">
            <button className='btn btn-dark w-100'>Register</button>
            </div>
          </form>
        </div>
      </div>
    </div>
        
</>
  )
}

export default Register
