import React, { Component } from 'react'

export class Login extends Component {
  constructor(props){
    super(props);
      this.state = {
        login:{
          username:'',
          password:'',
        },

      }
      this.fetchTasks = this.fetchTasks.bind(this)
      this.handleChange = this.handleChange.bind(this)
      this.handleSubmit = this.handleSubmit.bind(this)
      this.getCookie = this.getCookie.bind(this)


  };
  handleChange(e){
    var username = e.target.username
    var password = e.target.password
    console.log('Username:', username)
    console.log('Password:', value)

    this.setState({
      activeItem:{
        ...this.state.activeItem,
        title:value
      }
    })
  }

  handleSubmit(e){
    e.preventDefault()
    console.log('ITEM:', this.state.login)

    var csrftoken = this.getCookie('csrftoken')

    var url = 'http://127.0.0.1:8000/createItem'

    if(this.state.editing == true){
      url = `http://127.0.0.1:8000/updateItem/${this.state.activeItem.id}`
      this.setState({
        editing:false
      })
    }



    fetch(url, {
      method:'POST',
      headers:{
        'Content-type':'application/json',
        'X-CSRFToken':csrftoken,
      },
      body:JSON.stringify(this.state.activeItem)
    }).then((response)  => {
        this.fetchTasks()
        this.setState({
           activeItem:{
          id:null, 
          title:'',
          completed:false,
        }
        })
    }).catch(function(error){
      console.log('ERROR:', error)
    })

  }
  render() {
    return (
      <>
      <div className="container">
        <div className="row">
          <div className="col-md-3"></div>
          <div className="col-md-6">
          <h3>Login</h3>
            <form action="" onSubmit={this.handleSubmit} >
              <div className="form-group">
              <label htmlFor="id_username">Username</label>
              <input type="text"  className="form-control"  name="username"  value={this.state.login.username} autofocus="" maxlength="254" required="" id="id_username"/>
              </div>
              <div className="form-group">
              <label htmlFor="id_password">Password</label>
              <input className="form-control " type="password" name="password"  value={this.state.login.password} required="" id="id_password" />

              </div>
              <div className="form-group">
              <button className='btn btn-dark w-100' type="submit">Login</button>
              </div>
            </form>
          </div>
        </div>
      </div>
        
</>
    )
  }
}

export default Login
