import logo from './logo.svg';
import './App.css';

import React, { Component } from 'react'


class App extends Component {
  constructor(props){
    super(props);
    this.state = {
      todoList:[],
      activeItem:{
        id:null,
        title:'',
        completed:false,
      },
      editing:false,
    };
    this.fetchTasks= this.fetchTasks.bind(this);
    this.handleChange= this.handleChange.bind(this);
    this.formSubmit= this.formSubmit.bind(this);
    this.startEdit = this.startEdit.bind(this)
    this.getCookie = this.getCookie.bind(this)
    this.deleteItem = this.deleteItem.bind(this)

  };

  getCookie(name) {
    var cookieValue = null;
    if (document.cookie && document.cookie !== '') {
        var cookies = document.cookie.split(';');
        for (var i = 0; i < cookies.length; i++) {
            var cookie = cookies[i].trim();
            // Does this cookie string begin with the name we want?
            if (cookie.substring(0, name.length + 1) === (name + '=')) {
                cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
                break;
            }
        }
    }
    return cookieValue;
}
    componentDidMount(){
      this.fetchTasks()
    }


  //  setTimeout(this.fetchTasks, 5000); 
   // myTimeout = setTimeout(this.fetchTasks, milliseconds);


  componentWillMount(){
    this.fetchTasks()
  }
  fetchTasks(){

    console.log("Fetching...")
    fetch('http://127.0.0.1:8000/todolist')
    .then(response => response.json())
    .then(data => 
      //console.log(data)
      this.setState({
        todoList:data
      })


      )

  }



  formSubmit(e){
    e.preventDefault()

    if(this.state.activeItem.title === ''){
      console.log("Bu alan boş bırakılamaz")
      console.log(this.state.todoList)
    }
    else{

      console.log("Submit...")
      let item_id=this.state.activeItem.id

      console.log(this.state.editing)
      var csrftoken = this.getCookie('csrftoken')

      var url = 'http://127.0.0.1:8000/createItem'

      if(this.state.editing === true){
        url = `http://127.0.0.1:8000/updateItem/${this.state.activeItem.id}`

        this.setState({
          editing:false
        })
      }



      fetch(url, {
        method:'POST',
        headers:{
          'Content-type':'application/json',
          'X-CSRFToken':csrftoken,
        },
        body:JSON.stringify(this.state.activeItem)
      }).then((response)  => {
          this.fetchTasks()
          this.setState({
             activeItem:{
            id:null, 
            title:'',
            completed:false,
          }
          })
      }).catch(function(error){
        console.log('ERROR:', error)
      })



    }

  }

  startEdit = async (task)=>{ 

    this.setState({
      activeItem: task,
      editing: true,
    })

    console.log(this.state.activeItem)

  }



  deleteItem(task){
    console.log(task.id)

    var csrftoken = this.getCookie('csrftoken')
    console.log(csrftoken)

    fetch(`http://127.0.0.1:8000/deleteItem/${task.id}`, {
      method:'DELETE',
      headers:{
        'Content-type':'application/json',
        'X-CSRFToken':csrftoken,
      },
      body:JSON.stringify(task.id)
    }).then((response) =>{
      console.log(response)
      this.fetchTasks()
    })
  }





  handleChange(e){

    var value = e.target.value
    if(value === ''){
      this.setState(
        {
          activeItem:{
          title:value
        }
        }
      )
      console.log("Bu alan boş bırakılamaz")
    }
    else{
      console.log('Value:', value)
      this.setState(
        {
          activeItem:{
          title:value
        }
        }
      )
    }


  }

  strikeUnstrike(task){

    task.completed = !task.completed
    var csrftoken = this.getCookie('csrftoken')
    var url = `http://127.0.0.1:8000/updateItem/${task.id}`

      fetch(url, {
        method:'POST',
        headers:{
          'Content-type':'application/json',
          'X-CSRFToken':csrftoken,
        },
        body:JSON.stringify({'completed': task.completed, 'title':task.title})
      }).then(() => {
        this.fetchTasks()
      })

    console.log('TASK:', task.completed)
  }



  render() {
   var tasks = this.state.todoList
   var self = this
    return (
      <>

        <nav className='navbar bg-dark text-white'>
        <h4><img src={logo} alt="react-logo" className='r-logo' />REACT</h4>
        </nav>
      <div className='container'>
        <div className="row">
            <div className="col-md-3">
              </div>


            <div className="col-md-6">
                  <div className="task-container">

                        <div className="form-wrapper">
                        <h5>Todo App </h5>
                                <form onSubmit={this.formSubmit}  className='mt-5'>
                                <div className="flex-wrapper">

                                  <input onChange={this.handleChange} id="title"  value={this.state.activeItem.title} className='form-control' name="title"  type="text" />

                                  <button className='btn btn-dark'>Submit</button>

                                  </div>
                                </form>

                                {tasks.map(function(task, index){
                                          return(
                                            <div>
                                          <div key={index}  className='task-wrapper flex-wrapper'>
                                          <div key={index}  onClick={() => self.strikeUnstrike(task)} >
                                          {task.completed == false ? (
                                                    <span>{task.title}</span>

                                                  ) : (

                                                    <strike>{task.title}</strike>
                                                  )}
                                              </div>  
                                          <button onClick={() => self.deleteItem(task)} className="close">X</button>

                                        <div>
                                            <button onClick={() => self.startEdit(task)} className="btn btn-sm btn-outline-info ml-5">Edit</button>
                                        </div>

                                          </div>
                                          </div>
                                          )
                                        })}





                        </div>
                  </div>
              </div>

          </div>
      </div>


      </>
    )
  }
}



export default App;
